import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 45.2-817  Inspection of electric illumination equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-817.md","filePath":"vacode/45.2-817.md"}'),a={name:"vacode/45.2-817.md"},o=i('<h1 id="§-45-2-817-inspection-of-electric-illumination-equipment" tabindex="-1">§ 45.2-817 Inspection of electric illumination equipment. <a class="header-anchor" href="#§-45-2-817-inspection-of-electric-illumination-equipment" aria-label="Permalink to &quot;§ 45.2-817  Inspection of electric illumination equipment.&quot;">​</a></h1><p>Every lamp, extension light, and permissible form of portable illumination, such as a cap lamp or flashlight, that is used for personal illumination underground shall be inspected by an authorized person at least once per week, and more often if necessary, to ensure safe operating conditions. When such equipment is located at the surface, it shall be inspected by an authorized person at least once per month, and more often if necessary, to ensure safe operating conditions. Any defect found shall be corrected.</p><p>1966, c. 594, § 45.1-85; 1978, c. 118; 1993, c. 442; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.173; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),r=[o];function c(l,s,p,f,d,u){return t(),n("div",null,r)}const m=e(a,[["render",c]]);export{h as __pageData,m as default};
