import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-823  Storage and use of flammable fluids and materials.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-823.md","filePath":"vacode/45.2-823.md"}'),o={name:"vacode/45.2-823.md"},l=t('<h1 id="§-45-2-823-storage-and-use-of-flammable-fluids-and-materials" tabindex="-1">§ 45.2-823 Storage and use of flammable fluids and materials. <a class="header-anchor" href="#§-45-2-823-storage-and-use-of-flammable-fluids-and-materials" aria-label="Permalink to &quot;§ 45.2-823  Storage and use of flammable fluids and materials.&quot;">​</a></h1><p>A. Each underground storage place for oil, grease, or flammable hydraulic fluid shall be of fireproof construction.</p><p>B. Oil, grease, and flammable hydraulic fluid that is kept underground for current use shall be kept in a closed metal container.</p><p>C. Provisions shall be made to prevent an accumulation of spilled oil or grease at any such storage place or at any location at which such material is used.</p><p>D. Oily rags, oily waste, and wastepaper shall be kept in closed metal containers until it is removed for disposal.</p><p>E. No gasoline, benzene, kerosene, or other flammable oil shall be used underground in powering machinery.</p><p>F. Every oxygen or acetylene bottle that is used underground shall be secured while in use. When stored underground, each oxygen or acetylene bottle shall be placed in a safe location, protected from physical damage, stored with its cap in place where such storage is provided for on the tank, and secured upright or elevated, whichever mine heights allow.</p><p>Code 1950, §§ 45-14.1, 45-43; 1954, c. 191; 1966, c. 594, §§ 45.1-89, 45.1-90, 45.1-100; 1974, c. 323; 1978, cc. 118, 729; 1981, c. 131; 1984, c. 590, § 45.1-89, c. 639; 1985, c. 500; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.205; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',8),i=[l];function s(n,c,d,p,u,f){return a(),r("div",null,i)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
