import{_ as e,o as a,c as n,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-829  Examinations of fans.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-829.md","filePath":"vacode/45.2-829.md"}'),i={name:"vacode/45.2-829.md"},o=t('<h1 id="§-45-2-829-examinations-of-fans" tabindex="-1">§ 45.2-829 Examinations of fans. <a class="header-anchor" href="#§-45-2-829-examinations-of-fans" aria-label="Permalink to &quot;§ 45.2-829  Examinations of fans.&quot;">​</a></h1><p>A. An authorized person shall conduct a daily inspection of each main fan and of the machinery connected with such fan. The person making the examination shall record such examination in a book prescribed for this purpose or by other adequate means provided to permanently record the performance of the main fan and to give warning of an interruption to a fan. No such daily examination is required on any day in which no person goes underground, except that the examination shall be completed prior to any person entering the mine if no examination was made on the previous day.</p><p>B. Any place ventilated by means of a blower fan shall be examined for methane by a certified person before the fan is started at the beginning of the shift and after any interruption of fan operation that lasts for five minutes or more during the shift.</p><p>C. Each blower fan and its tubing shall be inspected at least twice during each working shift by a certified person.</p><p>Code 1950, §§ 45-60.1, 45-60.2; 1954, c. 191; 1966, c. 594, §§ 45.1-54, 45.1-55; 1978, c. 120; 1988, c. 597; 1993, c. 442; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.211; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),r=[o];function s(c,f,h,d,l,p){return a(),n("div",null,r)}const g=e(i,[["render",s]]);export{_ as __pageData,g as default};
