import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-831  Notice of hazardous conditions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-831.md","filePath":"vacode/45.2-831.md"}'),i={name:"vacode/45.2-831.md"},r=t('<h1 id="§-45-2-831-notice-of-hazardous-conditions" tabindex="-1">§ 45.2-831 Notice of hazardous conditions. <a class="header-anchor" href="#§-45-2-831-notice-of-hazardous-conditions" aria-label="Permalink to &quot;§ 45.2-831  Notice of hazardous conditions.&quot;">​</a></h1><p>The mine foreman shall give prompt attention to the removal of each hazardous condition reported to him by any person working in the mine. If it is impracticable to remove a hazardous condition at once, the mine foreman shall notify every person whose safety is threatened by such hazardous condition to remain away from the portion of the mine where the hazardous condition exists.</p><p>Code 1950, §§ 45-35, 45-40; 1954, c. 191; 1966, c. 594, § 45.1-17; 1978, c. 729; 1993, c. 170; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.214; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),n=[r];function c(s,h,d,l,_,f){return o(),a("div",null,n)}const u=e(i,[["render",c]]);export{m as __pageData,u as default};
