import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 45.2-840  Crosscuts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-840.md","filePath":"vacode/45.2-840.md"}'),o={name:"vacode/45.2-840.md"},s=a('<h1 id="§-45-2-840-crosscuts" tabindex="-1">§ 45.2-840 Crosscuts. <a class="header-anchor" href="#§-45-2-840-crosscuts" aria-label="Permalink to &quot;§ 45.2-840  Crosscuts.&quot;">​</a></h1><p>A. Crosscuts shall be made between entries and between rooms as provided in the approved roof control plan.</p><p>B. Every crosscut between an intake and a return air course shall be closed, except the one nearest the face. A crosscut between rooms shall be closed where necessary to provide adequate ventilation at the working face.</p><p>C. Where practicable, a crosscut shall be provided at or near the face of each entry or room before the place is abandoned.</p><p>D. No entry or room shall be started off an entry beyond the last open crosscut.</p><p>Code 1950, § 45-60.4; 1954, c. 191; 1966, c. 594, § 45.1-59; 1978, c. 729; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.223; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),c=[s];function n(i,l,p,d,h,_){return r(),t("div",null,c)}const b=e(o,[["render",n]]);export{u as __pageData,b as default};
