import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 45.2-843  Line brattice.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-843.md","filePath":"vacode/45.2-843.md"}'),o={name:"vacode/45.2-843.md"},i=r('<h1 id="§-45-2-843-line-brattice" tabindex="-1">§ 45.2-843 Line brattice. <a class="header-anchor" href="#§-45-2-843-line-brattice" aria-label="Permalink to &quot;§ 45.2-843  Line brattice.&quot;">​</a></h1><p>A. Substantially constructed line brattice shall be used from the last open crosscut of an entry or room when necessary to provide adequate ventilation for the miners and to remove gases. Any line brattice that is damaged by a fall or otherwise shall be repaired promptly.</p><p>B. The space between the line brattice and the rib shall be large enough to permit the flow of a sufficient volume of air to keep the working face clear of flammable and noxious gases.</p><p>C. Brattice cloth that is used underground shall be of flame-resistant material.</p><p>D. An accumulation of methane shall be moved only by means of properly installed line brattice or other approved method.</p><p>Code 1950, § 45-60.4; 1954, c. 191; 1966, c. 594, § 45.1-61; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.226; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),n=[i];function l(s,c,p,h,d,f){return t(),a("div",null,n)}const m=e(o,[["render",l]]);export{b as __pageData,m as default};
