import{_ as e,o as a,c as i,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-847  Examination of mine for explosive gas and other hazardous conditions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-847.md","filePath":"vacode/45.2-847.md"}'),n={name:"vacode/45.2-847.md"},t=o('<h1 id="§-45-2-847-examination-of-mine-for-explosive-gas-and-other-hazardous-conditions" tabindex="-1">§ 45.2-847 Examination of mine for explosive gas and other hazardous conditions. <a class="header-anchor" href="#§-45-2-847-examination-of-mine-for-explosive-gas-and-other-hazardous-conditions" aria-label="Permalink to &quot;§ 45.2-847  Examination of mine for explosive gas and other hazardous conditions.&quot;">​</a></h1><p>A. Every certified person whose regular duties require him to inspect working places in any mine for hazardous conditions shall have in his possession and shall use, when underground, a permissible methane detector or other permissible device capable of detecting methane and oxygen deficiency.</p><p>B. A sufficient number of permissible methane detectors or other permissible devices capable of detecting methane shall be kept at each mine inby the last open crosscut. Every miner shall be trained in the operation of such devices. Every miner working inby the last open crosscut shall be certified by the Board of Coal Mining Examiners pursuant to § <a href="/vacode/45.2-520/">45.2-520</a> to conduct gas testing.</p><p>C. Every methane detector shall be maintained in permissible condition. Every methane detector shall be calibrated at least monthly in accordance with the manufacturer&#39;s recommendations. A record of such calibration shall be made in a book for this purpose kept at a surface location at the mine and maintained for one year.</p><p>Code 1950, §§ 45-32, 45-33, 45-60.4, 45-68.1, 45-69.7, 45-85.6; 1954, c. 191; 1966, c. 594, §§ 45.1-35, 45.1-65; 1978, cc. 118, 120; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.231; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),r=[t];function s(c,l,d,h,p,f){return a(),i("div",null,r)}const u=e(n,[["render",s]]);export{g as __pageData,u as default};
