import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-853  Checking system; tracking system.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-853.md","filePath":"vacode/45.2-853.md"}'),n={name:"vacode/45.2-853.md"},i=r('<h1 id="§-45-2-853-checking-system-tracking-system" tabindex="-1">§ 45.2-853 Checking system; tracking system. <a class="header-anchor" href="#§-45-2-853-checking-system-tracking-system" aria-label="Permalink to &quot;§ 45.2-853  Checking system; tracking system.&quot;">​</a></h1><p>A. Each mine shall have a personnel checking system that includes the following requirements:</p><ol><li><p>Every person underground shall have on his person a means of positive identification bearing a number recorded by the operator; and</p></li><li><p>An accurate record of the persons in the mine shall be kept on the surface in a place that will not be affected by an explosion. Such record shall consist of a written record, check board, lamp check, or time-clock record and shall bear a number identical to that carried by the person underground.</p></li></ol><p>B. Any mine-wide tracking system shall be maintained in usable and operative condition.</p><p>Code 1950, § 45-70; 1954, c. 191; 1966, c. 594, § 45.1-97; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.174; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0103" target="_blank" rel="noreferrer">103</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0397" target="_blank" rel="noreferrer">397</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),s=[i];function o(c,l,h,d,p,g){return a(),t("div",null,s)}const f=e(n,[["render",o]]);export{m as __pageData,f as default};
