import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-856  Smoking materials prohibited; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-856.md","filePath":"vacode/45.2-856.md"}'),o={name:"vacode/45.2-856.md"},n=a('<h1 id="§-45-2-856-smoking-materials-prohibited-penalty" tabindex="-1">§ 45.2-856 Smoking materials prohibited; penalty. <a class="header-anchor" href="#§-45-2-856-smoking-materials-prohibited-penalty" aria-label="Permalink to &quot;§ 45.2-856  Smoking materials prohibited; penalty.&quot;">​</a></h1><p>A. No miner or other person shall smoke or carry or possess underground any smoker&#39;s articles or any match, lighter, or similar material generally used for igniting smoker&#39;s articles. Violation of this subsection is a Class 6 felony.</p><p>B. Each operator shall institute a smoker search program, approved by the Chief, to ensure that no person entering the underground area of the mine carries any smoking material, match, or lighter.</p><p>C. Any person entering or present in any underground area of a coal mine shall, by his entry into such underground area of the mine, be subject to a search of his person, including any personal property that is in any underground area of the mine at any time he is underground. Such search shall be conducted at the direction of the Chief by employees of the Department. It shall be limited in scope to the person and property of the person present underground at the time of the search and shall be for the purpose of enforcing the provisions of this section.</p><p>D. This section shall not prohibit the possession of equipment used solely for the operation of a flame safety lamp or for welding or cutting.</p><p>1993, c. 389, § 45.1-98.1; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.177; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0569" target="_blank" rel="noreferrer">569</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),i=[n];function s(l,h,p,c,d,m){return r(),t("div",null,i)}const u=e(o,[["render",s]]);export{g as __pageData,u as default};
