import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-861  First aid equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-861.md","filePath":"vacode/45.2-861.md"}'),r={name:"vacode/45.2-861.md"},s=i('<h1 id="§-45-2-861-first-aid-equipment" tabindex="-1">§ 45.2-861 First aid equipment. <a class="header-anchor" href="#§-45-2-861-first-aid-equipment" aria-label="Permalink to &quot;§ 45.2-861  First aid equipment.&quot;">​</a></h1><p>Each mine shall have adequate supplies of first aid equipment as determined by the Chief. Such supplies shall be located on the surface, at the bottom of each shaft and slope, and at other strategic locations near the working faces, as the Chief prescribes. Such first aid supplies shall be encased in suitable sanitary receptacles designed to be reasonably dust-tight and moisture-proof. Such supplies shall be available for use of any person employed in the mine. No first aid material shall be removed or diverted without authorization except in case of injury at the mine.</p><p>Code 1950, § 45-69.6; 1954, c. 191; 1966, c. 594, § 45.1-101; 1978, c. 94; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.197; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',3),o=[s];function n(l,c,d,p,h,f){return a(),t("div",null,o)}const m=e(r,[["render",n]]);export{_ as __pageData,m as default};
