import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-862  Attention to injured persons.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-862.md","filePath":"vacode/45.2-862.md"}'),a={name:"vacode/45.2-862.md"},n=o('<h1 id="§-45-2-862-attention-to-injured-persons" tabindex="-1">§ 45.2-862 Attention to injured persons. <a class="header-anchor" href="#§-45-2-862-attention-to-injured-persons" aria-label="Permalink to &quot;§ 45.2-862  Attention to injured persons.&quot;">​</a></h1><p>A. When an injury occurs underground, the injured person shall be brought promptly to the surface. Prompt medical attention shall be provided in the event of injury, and adequate facilities shall be made available for transporting such injured person to a hospital if necessary.</p><p>B. Safe transportation shall be provided to carry an injured person from the site where the injury occurred to the surface of the mine.</p><p>C. The operator of each mine shall post directional signs that are conspicuously located to identify the routes of ingress to and egress from any mine located off of a public road.</p><p>Code 1950, § 45-69.6; 1954, c. 191; 1966, c. 594, § 45.1-101; 1978, c. 94; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.198; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),i=[n];function s(c,l,p,d,h,f){return t(),r("div",null,i)}const g=e(a,[["render",s]]);export{_ as __pageData,g as default};
