import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-905  Areas with safety or health hazards; duties of surface mine foreman.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-905.md","filePath":"vacode/45.2-905.md"}'),i={name:"vacode/45.2-905.md"},n=t('<h1 id="§-45-2-905-areas-with-safety-or-health-hazards-duties-of-surface-mine-foreman" tabindex="-1">§ 45.2-905 Areas with safety or health hazards; duties of surface mine foreman. <a class="header-anchor" href="#§-45-2-905-areas-with-safety-or-health-hazards-duties-of-surface-mine-foreman" aria-label="Permalink to &quot;§ 45.2-905  Areas with safety or health hazards; duties of surface mine foreman.&quot;">​</a></h1><p>A. Any hazardous condition shall be corrected promptly or the affected area shall be barricaded or posted with warning signs specifying the hazard and proper safety procedures. Any imminent danger that cannot be removed within a reasonable time shall be reported to the Chief by the quickest available means.</p><p>B. The surface mine foreman shall see that the requirements of the Act pertaining to his duties and to the health and safety of the miners are fully complied with at all times.</p><p>C. The surface mine foreman shall see that every miner employed to work at the mine, before beginning work therein, is aware of any hazardous condition incident to his work at the mine.</p><p>Code 1950, §§ 45-7, 45-12, 45-14.1, 45-35, 45-40, 45-61, 45-68.1, 45-68.4, 45-69, 45-73, 45-75, 45-78, 45-79, 45-81, 45-83; 1950, p. 156; 1954, c. 191; 1966, c. 594, §§ 45.1-17, 45.1-21, 45.1-42, 45.1-89; 1974, c. 323; 1976, c. 598; 1978, cc. 118, 222, 489, 729; 1982, c. 255; 1984, cc. 178, 590; 1985, c. 500; 1988, c. 577; 1993, cc. 170, 171, 442; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.258; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0103" target="_blank" rel="noreferrer">103</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0397" target="_blank" rel="noreferrer">397</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),o=[n];function s(h,c,l,f,d,p){return a(),r("div",null,o)}const _=e(i,[["render",s]]);export{m as __pageData,_ as default};
