import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-916  Equipment operation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-916.md","filePath":"vacode/45.2-916.md"}'),n={name:"vacode/45.2-916.md"},o=i('<h1 id="§-45-2-916-equipment-operation" tabindex="-1">§ 45.2-916 Equipment operation. <a class="header-anchor" href="#§-45-2-916-equipment-operation" aria-label="Permalink to &quot;§ 45.2-916  Equipment operation.&quot;">​</a></h1><p>A. Equipment operating speeds, conditions, and characteristics shall be prudent and consistent with the conditions of the roadway, grade, clearance, visibility, and traffic and the type and use of equipment.</p><p>B. Any vehicle that follows another vehicle shall do so at a safe distance; passing shall be limited to areas of adequate clearance and visibility.</p><p>C. Mobile equipment shall be operated under power control at all times and each mobile equipment operator shall have full control of the equipment while in motion.</p><p>D. Before starting or moving equipment, an equipment operator shall be certain by signal or other means that all persons are clear.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.269; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',6),r=[o];function l(s,p,c,d,h,_){return a(),t("div",null,r)}const u=e(n,[["render",l]]);export{m as __pageData,u as default};
