import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-917  Safety measures on equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-917.md","filePath":"vacode/45.2-917.md"}'),r={name:"vacode/45.2-917.md"},n=t('<h1 id="§-45-2-917-safety-measures-on-equipment" tabindex="-1">§ 45.2-917 Safety measures on equipment. <a class="header-anchor" href="#§-45-2-917-safety-measures-on-equipment" aria-label="Permalink to &quot;§ 45.2-917  Safety measures on equipment.&quot;">​</a></h1><p>A. Every rubber-tired or crawler-mounted piece of equipment shall have a rollover protective structure to the extent required by 30 C.F.R. § 77.403-1.</p><p>B. Each seat belt provided in mobile equipment shall be maintained in safe working condition. Every operator of such equipment shall wear a seat belt when the equipment is in motion.</p><p>C. Mobile equipment shall be equipped with adequate brakes and parking brakes.</p><p>D. Cab windows shall be of safe design, kept in good condition, and clean for adequate visibility.</p><p>E. Any tire shall be deflated before any repair on it is started, and adequate means shall be provided to prevent wheel-locking rims from creating a hazard during tire inflation.</p><p>F. An audible warning device and headlights shall be provided on each piece of self-propelled mobile equipment.</p><p>G. An automatic backup alarm that is audible above surrounding noise levels shall be provided on each piece of mobile equipment. An automatic reverse-activated strobe light may be substituted for an audible alarm when mobile equipment is operated at night.</p><p>H. Each piece of equipment that is raised for repairs or other work shall be securely blocked before any person positions himself where the falling of such equipment could create a hazardous condition.</p><p>Code 1950, § 45-69.1; 1954, c. 191; 1966, c. 594, § 45.1-73; 1979, c. 315; 1983, c. 70; 1985, c. 302; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.270; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',10),o=[n];function l(s,p,c,d,u,h){return a(),i("div",null,o)}const b=e(r,[["render",l]]);export{m as __pageData,b as default};
