import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 45.2-924  Equipment operation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-924.md","filePath":"vacode/45.2-924.md"}'),i={name:"vacode/45.2-924.md"},o=r('<h1 id="§-45-2-924-equipment-operation" tabindex="-1">§ 45.2-924 Equipment operation. <a class="header-anchor" href="#§-45-2-924-equipment-operation" aria-label="Permalink to &quot;§ 45.2-924  Equipment operation.&quot;">​</a></h1><p>A. If a truck spotter is used, he shall be well in the clear while any truck is backing into dumping position and dumping. Every truck spotter shall use lights at night to direct backing and dumping operations.</p><p>B. Every dipper, bucket, scraper blade, or similar movable part shall be secured or lowered to the ground when not in use.</p><p>C. Equipment that is to be hauled shall be loaded and protected so as to prevent sliding or spillage. When moving between work areas, the equipment shall be secured in the travel position.</p><p>D. Tow bars shall be used to tow heavy equipment and a safety chain shall be used in conjunction with each tow bar.</p><p>E. Dust control measures shall be taken so as to prevent the obstruction of visibility of any equipment operator.</p><p>F. No dipper, bucket, loading boom, or other heavy load shall be swung over the cab of haulage equipment until the driver is out of the cab and in a safe location, unless the equipment is designed specifically to protect the driver from falling material.</p><p>G. Lights, flares, or other warning devices shall be posted when parked equipment creates a hazard for other vehicles.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.277; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',9),n=[o];function s(l,p,c,h,d,u){return t(),a("div",null,n)}const m=e(i,[["render",s]]);export{b as __pageData,m as default};
