import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 45.2-925  Control of dust and combustible material.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-925.md","filePath":"vacode/45.2-925.md"}'),r={name:"vacode/45.2-925.md"},s=o('<h1 id="§-45-2-925-control-of-dust-and-combustible-material" tabindex="-1">§ 45.2-925 Control of dust and combustible material. <a class="header-anchor" href="#§-45-2-925-control-of-dust-and-combustible-material" aria-label="Permalink to &quot;§ 45.2-925  Control of dust and combustible material.&quot;">​</a></h1><p>A. Where a surface coal mining operation raises an excessive amount of dust into the air, such dust shall be allayed at its sources by the use of water, water with a wetting agent added to it, or another effective method.</p><p>B. Drilling in rock shall be done wet or other means of dust control shall be used.</p><p>C. Loose coal, coal dust, oil, grease, or other combustible materials shall not be permitted to accumulate excessively on equipment or surface structures.</p><p>Code 1950, §§ 45-77.1, 45-85.1; 1954, c. 191; 1966, c. 594, §§ 45.1-66, 45.1-87; 1979, c. 315; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.278; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0003" target="_blank" rel="noreferrer">3</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),i=[s];function l(n,c,d,u,_,f){return t(),a("div",null,i)}const p=e(r,[["render",l]]);export{m as __pageData,p as default};
