import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 45.2-926  Overhead high-potential power lines; surface transmission lines; electric wiring in surface buildings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-926.md","filePath":"vacode/45.2-926.md"}'),n={name:"vacode/45.2-926.md"},t=r('<h1 id="§-45-2-926-overhead-high-potential-power-lines-surface-transmission-lines-electric-wiring-in-surface-buildings" tabindex="-1">§ 45.2-926 Overhead high-potential power lines; surface transmission lines; electric wiring in surface buildings. <a class="header-anchor" href="#§-45-2-926-overhead-high-potential-power-lines-surface-transmission-lines-electric-wiring-in-surface-buildings" aria-label="Permalink to &quot;§ 45.2-926  Overhead high-potential power lines; surface transmission lines; electric wiring in surface buildings.&quot;">​</a></h1><p>A. Overhead high-potential power lines shall be (i) placed at least 15 feet above the ground and 20 feet above any driveway or haulage road, (ii) installed on insulators, and (iii) supported and guarded to prevent contact with other circuits.</p><p>B. Surface transmission lines shall be protected against short circuits and lightning.</p><p>C. Electric wiring in surface buildings shall be installed so as to prevent fire and contact hazards.</p><p>Code 1950, §§ 45-82, 45-82.3; 1954, c. 191; 1966, c. 594, § 45.1-75; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.279; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',5),s=[t];function l(o,c,d,h,p,g){return i(),a("div",null,s)}const _=e(n,[["render",l]]);export{f as __pageData,_ as default};
