import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 45.2-930  Electrical trailing cables.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-930.md","filePath":"vacode/45.2-930.md"}'),l={name:"vacode/45.2-930.md"},r=i('<h1 id="§-45-2-930-electrical-trailing-cables" tabindex="-1">§ 45.2-930 Electrical trailing cables. <a class="header-anchor" href="#§-45-2-930-electrical-trailing-cables" aria-label="Permalink to &quot;§ 45.2-930  Electrical trailing cables.&quot;">​</a></h1><p>A. Every trailing cable shall be provided with suitable short-circuit protection and a means of disconnecting power from the cable.</p><p>B Any temporary splice in a trailing cable shall be made in a workmanlike manner and shall be mechanically strong and well-insulated.</p><p>C. The number of temporary, unvulcanized splices in a trailing cable shall be limited to one.</p><p>D. Every permanent splice in a trailing cable shall be made mechanically strong, with adequate electrical conductivity and flexibility, and shall be effectively insulated and sealed so as to exclude moisture. The finished splice shall be vulcanized or otherwise treated with suitable materials to provide flame-resistant properties and good bonding to the outer jacket.</p><p>E. Every trailing cable shall be protected against mechanical injury.</p><p>Code 1950, § 45-82.5; 1954, c. 191; 1966, c. 594, § 45.1-84; 1978, c. 118; 1993, c. 442; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.283; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',7),n=[r];function c(o,s,d,p,h,_){return a(),t("div",null,n)}const m=e(l,[["render",c]]);export{g as __pageData,m as default};
