import{_ as e,o as r,c as t,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-937  Penetration of underground mines; testing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-937.md","filePath":"vacode/45.2-937.md"}'),a={name:"vacode/45.2-937.md"},o=n('<h1 id="§-45-2-937-penetration-of-underground-mines-testing" tabindex="-1">§ 45.2-937 Penetration of underground mines; testing. <a class="header-anchor" href="#§-45-2-937-penetration-of-underground-mines-testing" aria-label="Permalink to &quot;§ 45.2-937  Penetration of underground mines; testing.&quot;">​</a></h1><p>A. A qualified person shall test for the presence of methane and for a deficiency of oxygen, using an approved device, at the entrance to an auger hole or at a highwall miner entry when either such entry point penetrates a worked-out area of an underground mine.</p><p>B. If one percent or more of methane is detected or 19.5 percent or less of oxygen is found to exist, no further work shall be performed until the atmosphere has been made safe.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.290; 1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',4),i=[o];function s(l,c,f,d,g,p){return r(),t("div",null,i)}const u=e(a,[["render",s]]);export{_ as __pageData,u as default};
