import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 45.2-938  Safety precautions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/45.2-938.md","filePath":"vacode/45.2-938.md"}'),i={name:"vacode/45.2-938.md"},n=o('<h1 id="§-45-2-938-safety-precautions" tabindex="-1">§ 45.2-938 Safety precautions. <a class="header-anchor" href="#§-45-2-938-safety-precautions" aria-label="Permalink to &quot;§ 45.2-938  Safety precautions.&quot;">​</a></h1><p>A. No person shall enter an auger hole or highwall miner entry without prior approval from the Chief.</p><p>B. Every auger hole or highwall miner entry shall be blocked with highwall spoil or other suitable material before it is abandoned.</p><p>C. Every auger or highwall mining machine that is exposed to any highwall or explosion hazard shall be provided with worker protection from falling material and a mine explosion.</p><p>D. At least one person shall be assigned to observe the highwall for possible movement while ground personnel are working in a high-risk area in close proximity to the highwall.</p><p>E. All persons shall stay clear of any moving auger or highwall miner train, and no person shall pass over or under a moving train unless adequate crossing facilities are provided.</p><p>F. The ground control plan shall specify any spacing of holes, web design, and use of alignment control devices.</p><p>G. The ground control plan shall include other administrative, engineering, and source controls that are to be provided for safe operations.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0028" target="_blank" rel="noreferrer">28</a>, § 45.1-161.291; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0826" target="_blank" rel="noreferrer">826</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0387" target="_blank" rel="noreferrer">387</a>.</p>',9),t=[n];function l(s,p,h,c,g,d){return a(),r("div",null,t)}const u=e(i,[["render",l]]);export{_ as __pageData,u as default};
