import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1003 Illegal use of defective and unsafe equipment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1003.md","filePath":"vacode/46.2-1003.md"}'),i={name:"vacode/46.2-1003.md"},n=t('<h1 id="§-46-2-1003-illegal-use-of-defective-and-unsafe-equipment" tabindex="-1">§ 46.2-1003 Illegal use of defective and unsafe equipment. <a class="header-anchor" href="#§-46-2-1003-illegal-use-of-defective-and-unsafe-equipment" aria-label="Permalink to &quot;§ 46.2-1003 Illegal use of defective and unsafe equipment.&quot;">​</a></h1><p>A. It shall be unlawful for any person to use or have as equipment on a motor vehicle operated on a highway any device or equipment mentioned in § <a href="/vacode/46.2-1002/">46.2-1002</a> which is defective and in an unsafe condition.</p><p>B. For any summons issued for a violation of this section, the court may, in its discretion, dismiss the summons, where proof of compliance with this section is provided to the court on or before the court date.</p><p>C. No law-enforcement officer shall stop a motor vehicle for a violation of this section. No evidence discovered or obtained as the result of a stop in violation of this subsection, including evidence discovered or obtained with the operator&#39;s consent, shall be admissible in any trial, hearing, or other proceeding.</p><p>1960, c. 125, § 46.1-308.1; 1989, c. 727; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0051" target="_blank" rel="noreferrer">51</a>.</p>',5),r=[n];function s(c,l,d,f,h,p){return o(),a("div",null,r)}const v=e(i,[["render",s]]);export{_ as __pageData,v as default};
