import{_ as e,o as a,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 46.2-1008 Fees for approval certificates.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1008.md","filePath":"vacode/46.2-1008.md"}'),r={name:"vacode/46.2-1008.md"},i=t('<h1 id="§-46-2-1008-fees-for-approval-certificates" tabindex="-1">§ 46.2-1008 Fees for approval certificates. <a class="header-anchor" href="#§-46-2-1008-fees-for-approval-certificates" aria-label="Permalink to &quot;§ 46.2-1008 Fees for approval certificates.&quot;">​</a></h1><p>Any person who applies to the Superintendent for a certificate of approval required by this article shall pay a fee not to exceed the following amounts:</p><ol><li><p>For approval and recordation of headlights, warning devices, safety glass, signal devices, and other devices required by this title to be approved by the Superintendent and not provided for elsewhere in this section, $150.</p></li><li><p>For approval and recordation of taillights, spot lights or any other lighting devices, seventy-five dollars.</p></li><li><p>For approval and recordation of brake-testing and light-testing machines, $100 for each type approved.</p></li><li><p>For approval and recordation of safety lap belts and shoulder straps or harnesses or any combination lap belt and shoulder strap or harness, fifty dollars.</p></li><li><p>For approval and recordation of safety glasses, face shields, or goggles for motorcycle operators, fifty dollars.</p></li></ol><p>Fees collected under this section shall be used by the Superintendent in examining and testing devices to be approved and for maintaining and publishing necessary records.</p><p>Code 1950, § 46-316; 1956, c. 36; 1958, c. 541, § 46.1-314; 1962, c. 146; 1966, c. 37; 1968, c. 171; 1970, c. 25; 1989, c. 727.</p>',5),s=[i];function n(l,p,c,d,f,h){return a(),o("div",null,s)}const g=e(r,[["render",n]]);export{v as __pageData,g as default};
