import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-1014 Brake lights.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1014.md","filePath":"vacode/46.2-1014.md"}'),i={name:"vacode/46.2-1014.md"},o=a('<h1 id="§-46-2-1014-brake-lights" tabindex="-1">§ 46.2-1014 Brake lights. <a class="header-anchor" href="#§-46-2-1014-brake-lights" aria-label="Permalink to &quot;§ 46.2-1014 Brake lights.&quot;">​</a></h1><p>A. Every motor vehicle, trailer, or semitrailer, except an antique vehicle not originally equipped with a brake light, registered in the Commonwealth and operated on the highways in the Commonwealth shall be equipped with at least two brake lights of a type approved by the Superintendent. Such brake lights shall automatically exhibit a red or amber light plainly visible in clear weather from a distance of 500 feet to the rear of such vehicle when the brake is applied.</p><p>The provisions of this section shall not apply to motorcycles or autocycles equipped with brake lights as required by § <a href="/vacode/46.2-1012/">46.2-1012</a>.</p><p>B. No law-enforcement officer shall stop a motor vehicle, trailer, or semitrailer for a violation of this section, except that a law-enforcement officer may stop a vehicle if it displays no brake lights that meet the requirements set forth in subsection A. No evidence discovered or obtained as the result of a stop in violation of this subsection, including evidence discovered or obtained with the operator&#39;s consent, shall be admissible in any trial, hearing, or other proceeding.</p><p>1970, c. 17, § 46.1-262.1; 1972, c. 6; 1989, c. 727; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0054" target="_blank" rel="noreferrer">54</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0063" target="_blank" rel="noreferrer">63</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0964" target="_blank" rel="noreferrer">964</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0051" target="_blank" rel="noreferrer">51</a>.</p>',5),l=[o];function n(s,h,c,p,g,f){return t(),r("div",null,l)}const _=e(i,[["render",n]]);export{b as __pageData,_ as default};
