import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-1015 Lights on bicycles, electric personal assistive mobility devices, personal delivery devices, electric power-assisted bicycles, mopeds, and motorized skateboards or scooters.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1015.md","filePath":"vacode/46.2-1015.md"}'),a={name:"vacode/46.2-1015.md"},s=t('<h1 id="§-46-2-1015-lights-on-bicycles-electric-personal-assistive-mobility-devices-personal-delivery-devices-electric-power-assisted-bicycles-mopeds-and-motorized-skateboards-or-scooters" tabindex="-1">§ 46.2-1015 Lights on bicycles, electric personal assistive mobility devices, personal delivery devices, electric power-assisted bicycles, mopeds, and motorized skateboards or scooters. <a class="header-anchor" href="#§-46-2-1015-lights-on-bicycles-electric-personal-assistive-mobility-devices-personal-delivery-devices-electric-power-assisted-bicycles-mopeds-and-motorized-skateboards-or-scooters" aria-label="Permalink to &quot;§ 46.2-1015 Lights on bicycles, electric personal assistive mobility devices, personal delivery devices, electric power-assisted bicycles, mopeds, and motorized skateboards or scooters.&quot;">​</a></h1><p>A. Every bicycle, electric personal assistive mobility device, personal delivery device, electric power-assisted bicycle, moped, and motorized skateboard or scooter with handlebars when in use between sunset and sunrise shall be equipped with a headlight on the front emitting a white light visible in clear weather from a distance of at least 500 feet to the front and a red reflector visible from a distance of at least 600 feet to the rear when directly in front of lawful lower beams of headlights on a motor vehicle. Such lights and reflector shall be of types approved by the Superintendent.</p><p>In addition to the foregoing provisions of this section, a bicycle or its rider may be equipped with lights or reflectors. These lights may be steady burning or blinking.</p><p>B. Every bicycle, or its rider, shall be equipped with a taillight on the rear emitting a red light plainly visible in clear weather from a distance of at least 500 feet to the rear when in use between sunset and sunrise and operating on any highway with a speed limit of 35 mph or greater. Any such taillight shall be of a type approved by the Superintendent.</p><p>Code 1950, §§ 46-268, 46-270; 1958, c. 541, § 46.1-263; 1981, c. 585; 1989, c. 727; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0254" target="_blank" rel="noreferrer">254</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0947" target="_blank" rel="noreferrer">947</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0973" target="_blank" rel="noreferrer">973</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0381" target="_blank" rel="noreferrer">381</a>; 2017, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0251" target="_blank" rel="noreferrer">251</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0788" target="_blank" rel="noreferrer">788</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0780" target="_blank" rel="noreferrer">780</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1269" target="_blank" rel="noreferrer">1269</a>.</p>',5),o=[s];function l(n,c,d,p,h,g){return i(),r("div",null,o)}const v=e(a,[["render",l]]);export{f as __pageData,v as default};
