import{_ as r,o as i,c as h,z as e,a as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 46.2-1017 Dimension or marker lights and reflectors, generally.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1017.md","filePath":"vacode/46.2-1017.md"}'),s={name:"vacode/46.2-1017.md"},a=e("h1",{id:"§-46-2-1017-dimension-or-marker-lights-and-reflectors-generally",tabindex:"-1"},[t("§ 46.2-1017 Dimension or marker lights and reflectors, generally. "),e("a",{class:"header-anchor",href:"#§-46-2-1017-dimension-or-marker-lights-and-reflectors-generally","aria-label":'Permalink to "§ 46.2-1017 Dimension or marker lights and reflectors, generally."'},"​")],-1),o=e("p",null,"All motor vehicles, trailers, or semitrailers exceeding seven feet in width or the widest portion of which extends four inches beyond the front fender extremes shall be equipped with amber lights mounted at the extreme right and left front top corners of such vehicle. Each such light shall be visible in clear weather for a distance of at least 500 feet to the front of such vehicle. Such vehicles shall also be equipped with red lights mounted at the extreme right and left rear top corners of such vehicle. Each such light shall be visible in clear weather for at least 500 feet to the rear of such vehicle. Any tractor truck, however, need not be equipped with rear red dimension or marker lights. If the front or the rear of such vehicle is not the widest portion of the vehicle, the dimension or marker lights required in this section shall be mounted on the widest portions of the vehicle with the amber lights herein required visible from the front as herein required and the red lights herein required visible from the rear as herein required. The lights herein required shall be of a type approved by the Superintendent.",-1),n=e("p",null,"In addition to the lights required in this section, each such vehicle shall be equipped with amber reflectors located on each side thereof, at or near the front. Red reflectors shall be used on the rear of each such vehicle. Such reflectors shall be securely fastened to the vehicle not less than fifteen inches and not more than sixty inches from the ground. For a vehicle that is less than fifteen inches tall, however, such reflectors shall be securely fastened thereto at the highest point the structure of a vehicle will permit. The reflectors shall be of a type approved by the Superintendent.",-1),l=e("p",null,"If any vehicle is so constructed as to make compliance with the requirements of this section impractical, the lights and reflectors shall be placed on the vehicle in accordance with the Superintendent's regulations.",-1),c=e("p",null,"If any vehicle required by this section to be equipped with dimension or marker lights has installed on its rear, as close as practicable to the top of the vehicle and as close as practicable to the vertical centerline of the vehicle, three red identification lights of a type approved by the Superintendent, with the light centers spaced not less than six inches or more than twelve inches apart, the rear dimension or marker lights may be mounted at any height but must indicate as nearly as practicable the extreme width of the vehicle.",-1),d=e("p",null,[t("Code 1950, § 46-271; 1952, c. 652; 1958, c. 541, § 46.1-265; 1960, c. 156; 1974, c. 218; 1977, c. 383; 1989, c. 727; 1997, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0023",target:"_blank",rel:"noreferrer"},"23"),t(".")],-1),f=[a,o,n,l,c,d];function p(u,m,v,g,b,_){return i(),h("div",null,f)}const k=r(s,[["render",p]]);export{w as __pageData,k as default};
