import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 46.2-1029.1 Flashing of headlights on certain vehicles.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1029.1.md","filePath":"vacode/46.2-1029.1.md"}'),r={name:"vacode/46.2-1029.1.md"},h=i('<h1 id="§-46-2-1029-1-flashing-of-headlights-on-certain-vehicles" tabindex="-1">§ 46.2-1029.1 Flashing of headlights on certain vehicles. <a class="header-anchor" href="#§-46-2-1029-1-flashing-of-headlights-on-certain-vehicles" aria-label="Permalink to &quot;§ 46.2-1029.1 Flashing of headlights on certain vehicles.&quot;">​</a></h1><p>Emergency vehicles as defined in subsection C of § <a href="http://law.lis.virginia.gov/vacode/46.2-920/" target="_blank" rel="noreferrer">46.2-920</a> may be equipped with the means to flash their headlights when their warning lights are activated if (i) the headlights are wired to allow either the high beam or low beam to flash, but not both, and (ii) the headlight system includes a switch or device which prevents flashing of headlights when headlights are required to be lighted under § <a href="http://law.lis.virginia.gov/vacode/46.2-1030/" target="_blank" rel="noreferrer">46.2-1030</a>.</p><p>The provisions of clause (ii) above shall not apply in the City of Chesapeake, the City of Portsmouth, the City of Poquoson, or the County of York.</p><p>1989, c. 47; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0069" target="_blank" rel="noreferrer">69</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0121" target="_blank" rel="noreferrer">121</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0209" target="_blank" rel="noreferrer">209</a>.</p>',4),o=[h];function n(l,s,c,g,d,f){return a(),t("div",null,o)}const v=e(r,[["render",n]]);export{p as __pageData,v as default};
