import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-1029.2 Certain vehicles may be equipped with secondary warning lights.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1029.2.md","filePath":"vacode/46.2-1029.2.md"}'),r={name:"vacode/46.2-1029.2.md"},n=t('<h1 id="§-46-2-1029-2-certain-vehicles-may-be-equipped-with-secondary-warning-lights" tabindex="-1">§ 46.2-1029.2 Certain vehicles may be equipped with secondary warning lights. <a class="header-anchor" href="#§-46-2-1029-2-certain-vehicles-may-be-equipped-with-secondary-warning-lights" aria-label="Permalink to &quot;§ 46.2-1029.2 Certain vehicles may be equipped with secondary warning lights.&quot;">​</a></h1><p>A. For purposes of this section, &quot;traffic incident management vehicle&quot; means any vehicle operating en route to or at the scene of a traffic accident or similar emergency that affects the travel lanes of a highway, provided that such vehicle is a (i) Department of Transportation vehicle operated by an incident management coordinator or (ii) vehicle operated pursuant to the Department of Transportation safety service patrol program or a contract with the Department of Transportation that includes traffic incident management services as defined in § <a href="/vacode/46.2-920.1/">46.2-920.1</a>. The provisions of § <a href="/vacode/46.2-920/">46.2-920</a> shall not apply to the operation of such traffic incident management vehicle.</p><p>B. In addition to other lights authorized by this article, any (i) fire apparatus, (ii) government-owned vehicle operated on official business by a local fire chief or other local fire official, (iii) emergency medical services vehicle, or (iv) traffic incident management vehicle may be equipped with alternating, blinking, or flashing red or red and white secondary warning lights mounted inside the vehicle&#39;s taillights, headlights, or marker lights of a type approved by the Superintendent of State Police.</p><p>C. In order to operate a traffic incident management vehicle with lighted warning lights pursuant to this section, a traffic incident management vehicle operator shall be required to (i) complete an initial emergency vehicle operators course from an approved course list prepared by the Department of Fire Programs, the Office of Emergency Medical Services, or an equivalent agency and (ii) recertify as an emergency vehicle operator every two years.</p><p>2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0502" target="_blank" rel="noreferrer">502</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0503" target="_blank" rel="noreferrer">503</a>; 2022, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0457" target="_blank" rel="noreferrer">457</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0458" target="_blank" rel="noreferrer">458</a>.</p>',5),o=[n];function c(l,s,h,p,d,f){return a(),i("div",null,o)}const v=e(r,[["render",c]]);export{m as __pageData,v as default};
