import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-1041 Restrictions as to solid rubber tires.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1041.md","filePath":"vacode/46.2-1041.md"}'),i={name:"vacode/46.2-1041.md"},o=a('<h1 id="§-46-2-1041-restrictions-as-to-solid-rubber-tires" tabindex="-1">§ 46.2-1041 Restrictions as to solid rubber tires. <a class="header-anchor" href="#§-46-2-1041-restrictions-as-to-solid-rubber-tires" aria-label="Permalink to &quot;§ 46.2-1041 Restrictions as to solid rubber tires.&quot;">​</a></h1><p>Every tire, other than a pneumatic tire, made of rubber on a motor vehicle moved on any highway shall have rubber on its entire traction surface at least one inch thick above the edge of the flange of the entire periphery. No motor vehicle equipped with such tires shall be operated on any highway in the Commonwealth unless a permit therefor is first secured from the Department of Transportation.</p><p>Code 1950, § 46-299; 1958, c. 541, § 46.1-295; 1989, c. 727; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0780" target="_blank" rel="noreferrer">780</a>.</p>',3),s=[o];function n(c,l,h,_,d,p){return t(),r("div",null,s)}const u=e(i,[["render",n]]);export{b as __pageData,u as default};
