import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1043.1 Tire loading.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1043.1.md","filePath":"vacode/46.2-1043.1.md"}'),i={name:"vacode/46.2-1043.1.md"},o=a('<h1 id="§-46-2-1043-1-tire-loading" tabindex="-1">§ 46.2-1043.1 Tire loading. <a class="header-anchor" href="#§-46-2-1043-1-tire-loading" aria-label="Permalink to &quot;§ 46.2-1043.1 Tire loading.&quot;">​</a></h1><p>No person shall operate for a commercial purpose a truck, trailer, or semitrailer with tires on any highway in the Commonwealth if any officer authorized to enforce overweight vehicle laws determines upon weighing such truck, trailer, or semitrailer that any such tire carries a weight greater than 125 percent of that marked on the sidewall of the tire.</p><p>The provisions of this section shall not apply to:</p><ol><li><p>Any vehicle that is being operated under the terms of a permit issued under Article 18 (§ <a href="http://law.lis.virginia.gov/vacode/46.2-1139/" target="_blank" rel="noreferrer">46.2-1139</a> et seq.) and is being operated at a reduced speed as required by the permit to compensate for the tire loading in excess of the manufacturer&#39;s rated capacity for the tire;</p></li><li><p>Any vehicle having a gross vehicle weight rating of 26,001 pounds or more;</p></li><li><p>Any manufactured home; or</p></li><li><p>Any vehicle not required to be registered.</p></li></ol><p>2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0430" target="_blank" rel="noreferrer">430</a>.</p>',5),n=[o];function l(s,c,h,d,p,_){return r(),t("div",null,n)}const m=e(i,[["render",l]]);export{g as __pageData,m as default};
