import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 46.2-1047 Muffler cutout, etc., illegal.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1047.md","filePath":"vacode/46.2-1047.md"}'),o={name:"vacode/46.2-1047.md"},l=a('<h1 id="§-46-2-1047-muffler-cutout-etc-illegal" tabindex="-1">§ 46.2-1047 Muffler cutout, etc., illegal. <a class="header-anchor" href="#§-46-2-1047-muffler-cutout-etc-illegal" aria-label="Permalink to &quot;§ 46.2-1047 Muffler cutout, etc., illegal.&quot;">​</a></h1><p>It shall be unlawful to sell or offer for sale any (i) muffler without interior baffle plates or other effective muffling device or (ii) gutted muffler, muffler cutout, or straight exhaust. It shall be unlawful for any person to operate on the highways in the Commonwealth a motor vehicle, moped, or motorized skateboard or foot-scooter equipped with a gutted muffler, muffler cutout, or straight exhaust.</p><p>Code 1950, § 46-306; 1950, p. 54; 1958, c. 541, § 46.1-302; 1964, c. 628; 1989, c. 727; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0529" target="_blank" rel="noreferrer">529</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0538" target="_blank" rel="noreferrer">538</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0783" target="_blank" rel="noreferrer">783</a>.</p>',3),i=[l];function f(c,u,n,s,h,_){return t(),r("div",null,i)}const g=e(o,[["render",f]]);export{d as __pageData,g as default};
