import{_ as e,o,c as t,O as s}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-1048 Pollution control systems or devices.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1048.md","filePath":"vacode/46.2-1048.md"}'),i={name:"vacode/46.2-1048.md"},a=s('<h1 id="§-46-2-1048-pollution-control-systems-or-devices" tabindex="-1">§ 46.2-1048 Pollution control systems or devices. <a class="header-anchor" href="#§-46-2-1048-pollution-control-systems-or-devices" aria-label="Permalink to &quot;§ 46.2-1048 Pollution control systems or devices.&quot;">​</a></h1><p>No motor vehicle registered in the Commonwealth and manufactured for the model year 1973 or for subsequent model years shall be operated on the highways in the Commonwealth unless it is equipped with an air pollution control system, device, or combination of such systems or devices installed in accordance with federal laws and regulations.</p><p>It shall be unlawful for any person to operate a motor vehicle, as herein described, on the highways in the Commonwealth with its pollution control system or device removed or otherwise rendered inoperable.</p><p>It shall be unlawful for any person to operate on the highways in the Commonwealth a motor vehicle, as described in this section, equipped with any emission control system or device unless it is of a type installed as standard factory equipment, or comparable to that designed for use upon the particular vehicle as standard factory equipment.</p><p>No motor vehicle, as described in this section, shall be issued a safety inspection approval sticker unless it is equipped as provided under the foregoing provisions of this section or if it violates this section.</p><p>The provisions of this section shall not prohibit or prevent shop adjustments or replacements of equipment for maintenance or repair or the conversion of engines to low polluting fuels, such as, but not limited to, natural gas or propane, so long as such action does not degrade the antipollution capabilities of the vehicle power system.</p><p>The provisions of this section shall not apply to converted electric vehicles.</p><p>1972, c. 640, § 46.1-301.1; 1973, c. 5; 1989, c. 727; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0177" target="_blank" rel="noreferrer">177</a>.</p>',8),n=[a];function r(l,c,p,h,d,u){return o(),t("div",null,n)}const v=e(i,[["render",r]]);export{f as __pageData,v as default};
