import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-105.2 Obtaining documents from the Department when not entitled thereto; penalty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-105.2.md","filePath":"vacode/46.2-105.2.md"}'),o={name:"vacode/46.2-105.2.md"},a=n('<h1 id="§-46-2-105-2-obtaining-documents-from-the-department-when-not-entitled-thereto-penalty" tabindex="-1">§ 46.2-105.2 Obtaining documents from the Department when not entitled thereto; penalty. <a class="header-anchor" href="#§-46-2-105-2-obtaining-documents-from-the-department-when-not-entitled-thereto-penalty" aria-label="Permalink to &quot;§ 46.2-105.2 Obtaining documents from the Department when not entitled thereto; penalty.&quot;">​</a></h1><p>A. It shall be unlawful for any person to obtain a Virginia driver&#39;s license, special identification card, vehicle registration, certificate of title, or other document issued by the Department if such person has not satisfied all legal and procedural requirements for the issuance thereof, or is otherwise not legally entitled thereto, including obtaining any document issued by the Department through the use of counterfeit, forged, or altered documents.</p><p>B. It shall be unlawful to aid any person to obtain any driver&#39;s license, special identification card, vehicle registration, certificate of title, or other document in violation of the provisions of subsection A.</p><p>C. It shall be unlawful to knowingly possess or use for any purpose any driver&#39;s license, special identification card, vehicle registration, certificate of title, or other document obtained in violation of the provisions of subsection A.</p><p>D. A violation of any provision of this section shall constitute a Class 2 misdemeanor if a person is charged and convicted of a violation of this section that involved the unlawful obtaining or possession of any document issued by the Department for the purpose of engaging in any age-limited activity, including but not limited to obtaining, possessing, or consuming alcoholic beverages. However, if a person is charged and convicted of any other violation of this section, such offense shall constitute a Class 6 felony.</p><p>E. Whenever it appears to the satisfaction of the Commissioner that any driver&#39;s license, special identification card, vehicle registration, certificate of title, or other document issued by the Department has been obtained in violation of this section, it may be cancelled by the Commissioner, who shall mail notice of the cancellation to the address of record maintained by the Department.</p><p>1992, c. 99; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0767" target="_blank" rel="noreferrer">767</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0819" target="_blank" rel="noreferrer">819</a>.</p>',7),r=[a];function s(l,c,h,d,f,p){return t(),i("div",null,r)}const m=e(o,[["render",s]]);export{g as __pageData,m as default};
