import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse(`{"title":"§ 46.2-1054 Suspension of objects or alteration of vehicle so as to obstruct driver's view.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1054.md","filePath":"vacode/46.2-1054.md"}`),i={name:"vacode/46.2-1054.md"},a=r('<h1 id="§-46-2-1054-suspension-of-objects-or-alteration-of-vehicle-so-as-to-obstruct-driver-s-view" tabindex="-1">§ 46.2-1054 Suspension of objects or alteration of vehicle so as to obstruct driver&#39;s view. <a class="header-anchor" href="#§-46-2-1054-suspension-of-objects-or-alteration-of-vehicle-so-as-to-obstruct-driver-s-view" aria-label="Permalink to &quot;§ 46.2-1054 Suspension of objects or alteration of vehicle so as to obstruct driver&#39;s view.&quot;">​</a></h1><p>A. It shall be unlawful for any person (i) to drive a motor vehicle on a highway in the Commonwealth with any object or objects, other than a rear view mirror, sun visor, or other equipment of the motor vehicle approved by the Superintendent, suspended from any part of the motor vehicle in such a manner as to substantially obstruct the driver&#39;s clear view of the highway through the windshield, the front side windows, or the rear window or (ii) to alter a passenger-carrying vehicle in such a manner as to obstruct the driver&#39;s view through the windshield. However, this section shall not apply (a) when the driver&#39;s clear view of the highway through the rear window is obstructed if such motor vehicle is equipped with a mirror on each side, so located as to reflect to the driver a view of the highway for at least 200 feet to the rear of such vehicle, (b) to safety devices installed on the windshields of vehicles owned by private waste haulers or local governments and used to transport solid waste, or (c) to bicycle racks installed on the front of any bus operated by any city, county, transit authority, or transit or transportation district. The provisions of clause (ii) shall not apply to the lawful immobilization of vehicles pursuant to § <a href="/vacode/46.2-1216/">46.2-1216</a> or <a href="/vacode/46.2-1231/">46.2-1231</a>.</p><p>B. No law-enforcement officer shall stop a motor vehicle for a violation of this section. No evidence discovered or obtained as the result of a stop in violation of this subsection, including evidence discovered or obtained with the operator&#39;s consent, shall be admissible in any trial, hearing, or other proceeding.</p><p>1960, c. 122, § 46.1-291.1; 1972, cc. 8, 844; 1987, c. 135; 1989, c. 727; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0273" target="_blank" rel="noreferrer">273</a>; 2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0510" target="_blank" rel="noreferrer">510</a>; 2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0051" target="_blank" rel="noreferrer">51</a>.</p>',4),s=[a];function n(c,h,l,d,v,f){return o(),t("div",null,s)}const b=e(i,[["render",n]]);export{u as __pageData,b as default};
