import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-1067 Within what distances brakes should stop vehicle.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1067.md","filePath":"vacode/46.2-1067.md"}'),s={name:"vacode/46.2-1067.md"},o=i('<h1 id="§-46-2-1067-within-what-distances-brakes-should-stop-vehicle" tabindex="-1">§ 46.2-1067 Within what distances brakes should stop vehicle. <a class="header-anchor" href="#§-46-2-1067-within-what-distances-brakes-should-stop-vehicle" aria-label="Permalink to &quot;§ 46.2-1067 Within what distances brakes should stop vehicle.&quot;">​</a></h1><p>On a dry, hard, approximately level stretch of highway free from loose material, the service braking system shall be capable of stopping a motor vehicle or combination of vehicles at all times and under all conditions of loading at a speed of 20 miles per hour within the following distances:</p><ol><li><p>Passenger motor vehicles, except buses and antique vehicles, 25 feet.</p></li><li><p>Buses, trucks, and tractor trucks, 40 feet.</p></li><li><p>Motor vehicles registered or qualified to be registered as antique vehicles, when equipped with two-wheel brakes, 45 feet; four-wheel brakes, 25 feet.</p></li><li><p>All combinations of vehicles, 40 feet.</p></li><li><p>Motorcycles or autocycles, 30 feet.</p></li></ol><p>Code 1950, § 46-284; 1958, c. 541, § 46.1-278; 1968, c. 164; 1970, c. 28; 1972, c. 3; 1989, c. 727; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>.</p>',4),r=[o];function l(c,n,h,d,p,f){return t(),a("div",null,r)}const v=e(s,[["render",l]]);export{u as __pageData,v as default};
