import{_ as e,o as t,c as r,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1072 Operation of vehicle without serial or identification number; requirements for stamping, cutting, or embossing numbers; regulations.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1072.md","filePath":"vacode/46.2-1072.md"}'),o={name:"vacode/46.2-1072.md"},a=i('<h1 id="§-46-2-1072-operation-of-vehicle-without-serial-or-identification-number-requirements-for-stamping-cutting-or-embossing-numbers-regulations" tabindex="-1">§ 46.2-1072 Operation of vehicle without serial or identification number; requirements for stamping, cutting, or embossing numbers; regulations. <a class="header-anchor" href="#§-46-2-1072-operation-of-vehicle-without-serial-or-identification-number-requirements-for-stamping-cutting-or-embossing-numbers-regulations" aria-label="Permalink to &quot;§ 46.2-1072 Operation of vehicle without serial or identification number; requirements for stamping, cutting, or embossing numbers; regulations.&quot;">​</a></h1><p>It shall be unlawful to sell or to drive on any highway in the Commonwealth any motor vehicle which does not have stamped on or cut into its motor its motor number or which does not bear a permanent serial or other identification number assigned by the manufacturer or by the Commissioner, or any trailer or semitrailer which does not bear a permanent serial or other identification number assigned by its manufacturer or the Commissioner. The number shall be stamped, cut, embossed, or attached in such a manner that it cannot be changed, altered, or removed without plainly showing evidence which would be readily detectable or which would destroy the attached plate. The number shall be die stamped, cut, or embossed into or attached to a permanent part of the vehicle which is easily accessible for verification. However, nonresident owners who are permitted to operate motor vehicles, trailers, or semitrailers without registration, under the registration provision relating to nonresidents contained in §§ <a href="http://law.lis.virginia.gov/vacode/46.2-655/" target="_blank" rel="noreferrer">46.2-655</a> through <a href="http://law.lis.virginia.gov/vacode/46.2-661/" target="_blank" rel="noreferrer">46.2-661</a> shall not be required to comply with this section before operating a motor vehicle, trailer, or semitrailer on the highways in the Commonwealth.</p><p>The Commissioner may adopt regulations to carry out the provisions of this section.</p><p>Code 1950, § 46-11; 1952, c. 545; 1958, c. 541, § 46.1-84; 1978, c. 294; 1989, c. 727; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0096" target="_blank" rel="noreferrer">96</a>.</p>',4),n=[a];function s(h,c,l,m,u,d){return t(),r("div",null,n)}const b=e(o,[["render",s]]);export{g as __pageData,b as default};
