import{_ as e,o as i,c as o,O as r}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 46.2-1078 Unlawful to operate motor vehicle, bicycle, electric personal assistive mobility device, electric power-assisted bicycle, or moped while using earphones.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1078.md","filePath":"vacode/46.2-1078.md"}'),t={name:"vacode/46.2-1078.md"},a=r('<h1 id="§-46-2-1078-unlawful-to-operate-motor-vehicle-bicycle-electric-personal-assistive-mobility-device-electric-power-assisted-bicycle-or-moped-while-using-earphones" tabindex="-1">§ 46.2-1078 Unlawful to operate motor vehicle, bicycle, electric personal assistive mobility device, electric power-assisted bicycle, or moped while using earphones. <a class="header-anchor" href="#§-46-2-1078-unlawful-to-operate-motor-vehicle-bicycle-electric-personal-assistive-mobility-device-electric-power-assisted-bicycle-or-moped-while-using-earphones" aria-label="Permalink to &quot;§ 46.2-1078 Unlawful to operate motor vehicle, bicycle, electric personal assistive mobility device, electric power-assisted bicycle, or moped while using earphones.&quot;">​</a></h1><p>It shall be unlawful for any person to operate a motor vehicle, bicycle, electric personal assistive mobility device, electric power-assisted bicycle, or moped on the highways in the Commonwealth while using earphones on or in both ears.</p><p>For the purpose of this section, &quot;earphones&quot; shall mean any device worn on or in both ears that converts electrical energy to sound waves or which impairs or hinders the person&#39;s ability to hear, but shall not include (i) any prosthetic device that aids the hard of hearing, (ii) earphones installed in helmets worn by motorcycle operators and riders and used as part of a communications system, or (iii) nonprosthetic, closed-ear, open-back, electronic noise-cancellation devices designed and used to enhance the hearing ability of persons who operate vehicles in high-noise environments, provided any such device is being worn by the operator of a vehicle with a gross vehicle weight rating of 26,000 pounds or more. The provisions of this section shall not apply to the driver of any emergency vehicle as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-920/" target="_blank" rel="noreferrer">46.2-920</a>.</p><p>Code 1950, § 46-219.1; 1950, p. 882; 1958, c. 541, § 46.1-202.1; 1989, c. 727; 1993, c. 126; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0834" target="_blank" rel="noreferrer">834</a>; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0254" target="_blank" rel="noreferrer">254</a>.</p>',4),s=[a];function c(n,l,h,p,d,v){return i(),o("div",null,s)}const f=e(t,[["render",c]]);export{b as __pageData,f as default};
