import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1087 Forfeiture of vehicles equipped with smoke projectors, etc.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1087.md","filePath":"vacode/46.2-1087.md"}'),a={name:"vacode/46.2-1087.md"},i=r('<h1 id="§-46-2-1087-forfeiture-of-vehicles-equipped-with-smoke-projectors-etc" tabindex="-1">§ 46.2-1087 Forfeiture of vehicles equipped with smoke projectors, etc. <a class="header-anchor" href="#§-46-2-1087-forfeiture-of-vehicles-equipped-with-smoke-projectors-etc" aria-label="Permalink to &quot;§ 46.2-1087 Forfeiture of vehicles equipped with smoke projectors, etc.&quot;">​</a></h1><p>Any motor vehicle found to be equipped with any device, appliance, equipment, or instrument, as mentioned in § <a href="http://law.lis.virginia.gov/vacode/46.2-1086/" target="_blank" rel="noreferrer">46.2-1086</a>, or equipped for the installation or attachment of any &quot;smoke screen&quot; or gas or flame emitting device, appliance, equipment, or instrument, as so mentioned, shall be forfeited to the Commonwealth, and upon being condemned as forfeited in proceedings under Chapter 22.1 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.1/" target="_blank" rel="noreferrer">19.2-386.1</a> et seq.) of Title 19.2, the proceeds of sale shall be disposed of according to law. No such forfeiture, however, shall take place unless the owner or operator knows that such vehicle is so equipped.</p><p>Code 1950, § 46-310; 1958, c. 541, § 46.1-306; 1989, c. 727; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',3),n=[i];function s(c,l,p,d,h,f){return t(),o("div",null,n)}const m=e(a,[["render",s]]);export{_ as __pageData,m as default};
