import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-1090.1 Warning lights on school buses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1090.1.md","filePath":"vacode/46.2-1090.1.md"}'),o={name:"vacode/46.2-1090.1.md"},r=i('<h1 id="§-46-2-1090-1-warning-lights-on-school-buses" tabindex="-1">§ 46.2-1090.1 Warning lights on school buses. <a class="header-anchor" href="#§-46-2-1090-1-warning-lights-on-school-buses" aria-label="Permalink to &quot;§ 46.2-1090.1 Warning lights on school buses.&quot;">​</a></h1><p>In addition to other lights authorized by law, school buses may be equipped with flashing white or amber warning lights of types authorized by the Board of Education after consultation with the Superintendent of State Police. These warning lights shall be installed in a manner authorized by the Board after consultation with the Superintendent and shall be lighted while the bus is transporting school children during periods of reduced visibility caused by atmospheric conditions other than darkness. These warning lights may also be lighted at other times while the bus is transporting school children. Drivers of motor vehicles approaching school buses displaying lighted warning lights authorized in this section shall not be required to stop except as required in §§ <a href="http://law.lis.virginia.gov/vacode/46.2-844/" target="_blank" rel="noreferrer">46.2-844</a> and <a href="http://law.lis.virginia.gov/vacode/46.2-859/" target="_blank" rel="noreferrer">46.2-859</a>.</p><p>1992, c. 159; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0065" target="_blank" rel="noreferrer">65</a>.</p>',3),n=[r];function s(h,l,c,d,g,p){return t(),a("div",null,n)}const b=e(o,[["render",s]]);export{u as __pageData,b as default};
