import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-1105 Width of vehicles generally; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1105.md","filePath":"vacode/46.2-1105.md"}'),i={name:"vacode/46.2-1105.md"},o=r('<h1 id="§-46-2-1105-width-of-vehicles-generally-exceptions" tabindex="-1">§ 46.2-1105 Width of vehicles generally; exceptions. <a class="header-anchor" href="#§-46-2-1105-width-of-vehicles-generally-exceptions" aria-label="Permalink to &quot;§ 46.2-1105 Width of vehicles generally; exceptions.&quot;">​</a></h1><p>A. No vehicle, including any load thereon, but excluding the mirror required by § <a href="http://law.lis.virginia.gov/vacode/46.2-1082/" target="_blank" rel="noreferrer">46.2-1082</a> and any warning device installed on a school bus pursuant to § <a href="http://law.lis.virginia.gov/vacode/46.2-1090/" target="_blank" rel="noreferrer">46.2-1090</a>, shall exceed a total outside width as follows:</p><ol><li><p>Passenger bus operated in an incorporated city or town when authorized under § <a href="http://law.lis.virginia.gov/vacode/46.2-1300/" target="_blank" rel="noreferrer">46.2-1300</a> -- 102 inches;</p></li><li><p>School buses -- 100 inches;</p></li><li><p>Vehicles hauling boats or other watercraft -- 102 inches;</p></li><li><p>Other vehicles -- 102 inches.</p></li></ol><p>B. Notwithstanding subsection A, a travel trailer as defined in § <a href="http://law.lis.virginia.gov/vacode/46.2-1500/" target="_blank" rel="noreferrer">46.2-1500</a> or a motor home may exceed 102 inches if such excess width is attributable to an appurtenance that extends no more than six inches beyond the body of the vehicle. For the purposes of this subsection, &quot;appurtenance&quot; includes (i) an awning and its support hardware and (ii) any appendage that is installed by the manufacturer or dealer intended to be an integral part of a motor home or travel trailer, but does not include any item that is temporarily attached to the exterior of the vehicle by the vehicle&#39;s owner for the purposes of transporting the item from one location to another.</p><p>Code 1950, § 46-326; 1950, p. 982; 1952, c. 403; 1956, c. 483; 1958, c. 541, § 46.1-328; 1960, c. 591; 1968, c. 333; 1973, c. 183; 1979, c. 70; 1983, c. 515; 1985, c. 426; 1986, c. 72; 1989, c. 727; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0819" target="_blank" rel="noreferrer">819</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0446" target="_blank" rel="noreferrer">446</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0506" target="_blank" rel="noreferrer">506</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0151" target="_blank" rel="noreferrer">151</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0615" target="_blank" rel="noreferrer">615</a>.</p>',5),n=[o];function l(c,s,h,d,p,g){return t(),a("div",null,n)}const v=e(i,[["render",l]]);export{u as __pageData,v as default};
