import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1114 Length of watercraft transporters; operation on certain highways.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1114.md","filePath":"vacode/46.2-1114.md"}'),o={name:"vacode/46.2-1114.md"},n=r('<h1 id="§-46-2-1114-length-of-watercraft-transporters-operation-on-certain-highways" tabindex="-1">§ 46.2-1114 Length of watercraft transporters; operation on certain highways. <a class="header-anchor" href="#§-46-2-1114-length-of-watercraft-transporters-operation-on-certain-highways" aria-label="Permalink to &quot;§ 46.2-1114 Length of watercraft transporters; operation on certain highways.&quot;">​</a></h1><p>Watercraft transporters shall not exceed a length of 65 feet when operated on any interstate highway or on any highway as designated by the Commonwealth Transportation Board. Stinger-steered watercraft transporters shall not exceed a length of 75 feet when operated on any interstate highway or on any highway designated by the Commonwealth Transportation Board. In addition, watercraft may be transported on a truck/trailer combination no more than 65 feet long when operated on any interstate highway or on any highway designated by the Commonwealth Transportation Board. Any such vehicle shall display a sign of a size and type approved by the Commissioner of Highways warning that the vehicle is an over-length vehicle. However, an additional three-foot overhang shall be allowed beyond the front and a four-foot overhang shall be allowed beyond the rear of the vehicle. Such combinations shall have reasonable access to terminals, facilities for food, fuel, repairs, and rest as designated by the Commissioner of Highways.</p><p>1986, c. 72, § 46.1-330.1; 1989, cc. 645, 727; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0456" target="_blank" rel="noreferrer">456</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0585" target="_blank" rel="noreferrer">585</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>.</p>',3),i=[n];function s(h,l,c,g,d,f){return a(),t("div",null,i)}const w=e(o,[["render",s]]);export{_ as __pageData,w as default};
