import{_ as e,o as i,c as t,O as o}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 46.2-1129.1 Further extension of weight limits for certain vehicles utilizing an auxiliary power unit or other idle reduction technology.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1129.1.md","filePath":"vacode/46.2-1129.1.md"}'),r={name:"vacode/46.2-1129.1.md"},n=o('<h1 id="§-46-2-1129-1-further-extension-of-weight-limits-for-certain-vehicles-utilizing-an-auxiliary-power-unit-or-other-idle-reduction-technology" tabindex="-1">§ 46.2-1129.1 Further extension of weight limits for certain vehicles utilizing an auxiliary power unit or other idle reduction technology. <a class="header-anchor" href="#§-46-2-1129-1-further-extension-of-weight-limits-for-certain-vehicles-utilizing-an-auxiliary-power-unit-or-other-idle-reduction-technology" aria-label="Permalink to &quot;§ 46.2-1129.1 Further extension of weight limits for certain vehicles utilizing an auxiliary power unit or other idle reduction technology.&quot;">​</a></h1><p>Any motor vehicle that utilizes an auxiliary power unit or other idle reduction technology in order to promote reduction of fuel use and emissions due to engine idling shall be allowed up to an additional 550 pounds total in gross, single axle, tandem axle, or bridge formula weight limits.</p><p>To be eligible for this exception, the operator of the vehicle must be able to prove (i) by written certification, the weight of the auxiliary power unit or other idle reduction technology unit and (ii) by demonstration or written certification, that such idle reduction technology is fully functional at all times.</p><p>Certification of the weight of the auxiliary power unit must be available to law-enforcement officials if the vehicle is found in violation of applicable weight laws. The additional weight allowed cannot exceed 550 pounds or the weight certified, whichever is less.</p><p>For purposes of this section, &quot;auxiliary power unit&quot; means a mechanical or electrical device affixed to a motor vehicle that is designed to be used to generate an alternative source of power for any of the motor vehicle&#39;s systems other than the primary propulsion engine, and &quot;idle reduction technology&quot; refers to a technology that allows engine operators to refrain from long-duration idling of the main propulsion engine by using an alternative technology.</p><p>2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0092" target="_blank" rel="noreferrer">92</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0118" target="_blank" rel="noreferrer">118</a>.</p>',6),a=[n];function l(c,h,s,u,d,f){return i(),t("div",null,a)}const m=e(r,[["render",l]]);export{p as __pageData,m as default};
