import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 46.2-1129.2 Further extension of weight limits for vehicles fueled by natural gas or powered by means of electric battery power.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1129.2.md","filePath":"vacode/46.2-1129.2.md"}'),i={name:"vacode/46.2-1129.2.md"},o=r('<h1 id="§-46-2-1129-2-further-extension-of-weight-limits-for-vehicles-fueled-by-natural-gas-or-powered-by-means-of-electric-battery-power" tabindex="-1">§ 46.2-1129.2 Further extension of weight limits for vehicles fueled by natural gas or powered by means of electric battery power. <a class="header-anchor" href="#§-46-2-1129-2-further-extension-of-weight-limits-for-vehicles-fueled-by-natural-gas-or-powered-by-means-of-electric-battery-power" aria-label="Permalink to &quot;§ 46.2-1129.2 Further extension of weight limits for vehicles fueled by natural gas or powered by means of electric battery power.&quot;">​</a></h1><p>A. On any highway other than an interstate highway, any motor vehicle that is fueled, wholly or partially, by natural gas or powered primarily by means of electric battery power shall be allowed up to an additional 2,000 pounds total in gross, single axle, tandem axle, or bridge formula weight limits, provided that such weight is on the power unit.</p><p>To be eligible for this exception, the operator of the vehicle must be able to demonstrate that the vehicle is a natural gas vehicle, a bi-fuel vehicle using natural gas, a vehicle that has been converted to a natural gas vehicle, or a vehicle that is powered primarily by means of electric battery power.</p><p>B. On an interstate highway, any motor vehicle that is fueled primarily by natural gas or powered primarily by means of electric battery power may exceed the weight limits provided in § <a href="/vacode/46.2-1127/">46.2-1127</a> by up to an additional 2,000 pounds, provided that such weight is on the power unit. However, the gross weight of such vehicle shall not exceed 82,000 pounds.</p><p>2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0133" target="_blank" rel="noreferrer">133</a>.</p>',5),l=[o];function n(s,h,c,p,d,f){return t(),a("div",null,l)}const b=e(i,[["render",n]]);export{g as __pageData,b as default};
