import{_ as e,o as i,c as r,O as t}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-1141 Overweight permits for containerized freight and fluid milk.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1141.md","filePath":"vacode/46.2-1141.md"}'),a={name:"vacode/46.2-1141.md"},n=t('<h1 id="§-46-2-1141-overweight-permits-for-containerized-freight-and-fluid-milk" tabindex="-1">§ 46.2-1141 Overweight permits for containerized freight and fluid milk. <a class="header-anchor" href="#§-46-2-1141-overweight-permits-for-containerized-freight-and-fluid-milk" aria-label="Permalink to &quot;§ 46.2-1141 Overweight permits for containerized freight and fluid milk.&quot;">​</a></h1><p>Permits to operate on the highways a vehicle exceeding the maximum weight specified in this title shall be granted for a vehicle hauling containerized cargo in a sealed, seagoing container bound to or from a seaport and has been or will be transported by marine shipment and for a tank vehicle hauling fluid milk. In order for a vehicle hauling containerized cargo in a sealed, seagoing container bound to or from a seaport to qualify for such a permit, the contents of such seagoing container shall not be changed from the time it is loaded by the consignor or his agents to the time it is delivered to the consignee or his agents. Cargo moving in vehicles conforming to specifications shown in this section shall be considered irreducible and eligible for permits under regulations of the Commissioner.</p><p>The fee for a permit issued under this section shall be as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-1140.1/" target="_blank" rel="noreferrer">46.2-1140.1</a>. Only the Commissioner may issue a permit under this section.</p><p>For purposes of this section &quot;tank vehicle&quot; has the same meaning ascribed to it in § <a href="http://law.lis.virginia.gov/vacode/46.2-341.4/" target="_blank" rel="noreferrer">46.2-341.4</a>.</p><p>1973, c. 62, § 46.1-343.3; 1989, c. 727; 1991, 1st Sp. Sess., c. 17; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0146" target="_blank" rel="noreferrer">146</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0554" target="_blank" rel="noreferrer">554</a>.</p>',5),o=[n];function s(l,h,c,g,d,f){return i(),r("div",null,o)}const u=e(a,[["render",s]]);export{m as __pageData,u as default};
