import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 46.2-1144.1 Overweight permits for tank wagons.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1144.1.md","filePath":"vacode/46.2-1144.1.md"}'),i={name:"vacode/46.2-1144.1.md"},o=r('<h1 id="§-46-2-1144-1-overweight-permits-for-tank-wagons" tabindex="-1">§ 46.2-1144.1 Overweight permits for tank wagons. <a class="header-anchor" href="#§-46-2-1144-1-overweight-permits-for-tank-wagons" aria-label="Permalink to &quot;§ 46.2-1144.1 Overweight permits for tank wagons.&quot;">​</a></h1><p>The Commissioner, upon written application and payment of a fee by the owner of tank wagon vehicles as defined in § <a href="http://law.lis.virginia.gov/vacode/58.1-2201/" target="_blank" rel="noreferrer">58.1-2201</a>, shall issue overweight permits for operation of said vehicles.</p><p>The fee for such permit shall be as provided in § <a href="http://law.lis.virginia.gov/vacode/46.2-1140.1/" target="_blank" rel="noreferrer">46.2-1140.1</a>.</p><p>No permit issued under this section shall authorize a single axle weight of more than 24,000 pounds and a total gross weight in excess of 40,000 pounds. Permits issued under this section shall be valid for one year from the date of issuance. No permit issued under this section shall authorize violation of any weight limitation, promulgated and posted in accordance with § <a href="http://law.lis.virginia.gov/vacode/46.2-1130/" target="_blank" rel="noreferrer">46.2-1130</a>, applicable to bridges or culverts. This permit shall not be combined with any other overweight permit or extension of weight limits.</p><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0033" target="_blank" rel="noreferrer">33</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>.</p>',5),n=[o];function s(l,h,p,c,g,d){return a(),t("div",null,n)}const v=e(i,[["render",s]]);export{_ as __pageData,v as default};
