import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-1145 Overweight permits for certain trucks operated by Arlington County.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1145.md","filePath":"vacode/46.2-1145.md"}'),a={name:"vacode/46.2-1145.md"},i=r('<h1 id="§-46-2-1145-overweight-permits-for-certain-trucks-operated-by-arlington-county" tabindex="-1">§ 46.2-1145 Overweight permits for certain trucks operated by Arlington County. <a class="header-anchor" href="#§-46-2-1145-overweight-permits-for-certain-trucks-operated-by-arlington-county" aria-label="Permalink to &quot;§ 46.2-1145 Overweight permits for certain trucks operated by Arlington County.&quot;">​</a></h1><p>The Commissioner, upon written application by Arlington County, shall issue without a fee to such county a permit authorizing the county&#39;s operation of vehicles used for hauling household waste and vehicles used for highway or utility construction, operation, or maintenance upon the highways of such county at gross weights exceeding those set forth in § <a href="http://law.lis.virginia.gov/vacode/46.2-1126/" target="_blank" rel="noreferrer">46.2-1126</a>. Permits issued hereunder shall specify that vehicles with two axles may have a maximum gross weight of no more than 48,000 pounds and a single axle weight of not more than 24,000 pounds and that vehicles with three axles may have a maximum gross weight of not more than 60,000 pounds and a single axle weight of not more than 24,000 pounds and a tandem axle weight of not more than 40,000 pounds.</p><p>The permit shall not designate the route to be traversed nor contain restrictions or conditions not applicable to other vehicles in their general use of the highways. Each vehicle, when loaded according to the provisions of a permit issued under this section shall be operated at a reduced speed of 10 miles per hour slower than the legal speed limit in 55, 45, and 35 miles per hour speed limit zones.</p><p>1973, c. 62, § 46.1-343.3; 1989, c. 727; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>.</p>',4),n=[i];function s(h,l,c,p,d,u){return t(),o("div",null,n)}const m=e(a,[["render",s]]);export{f as __pageData,m as default};
