import{_ as e,o as t,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-1146 Excess height and length permits for haulers of certain imported goods.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1146.md","filePath":"vacode/46.2-1146.md"}'),a={name:"vacode/46.2-1146.md"},i=o('<h1 id="§-46-2-1146-excess-height-and-length-permits-for-haulers-of-certain-imported-goods" tabindex="-1">§ 46.2-1146 Excess height and length permits for haulers of certain imported goods. <a class="header-anchor" href="#§-46-2-1146-excess-height-and-length-permits-for-haulers-of-certain-imported-goods" aria-label="Permalink to &quot;§ 46.2-1146 Excess height and length permits for haulers of certain imported goods.&quot;">​</a></h1><p>The Commissioner and local authorities of cities and towns in their respective jurisdictions, upon written application by the owners or operators of motor vehicles used to transport items arriving at a Virginia port by ship from overseas points of origin and consigned to an assembly plant in this Commonwealth, shall issue without cost permits for the operation of such motor vehicles on the highways if those vehicles do not exceed the height limitation set forth in § <a href="http://law.lis.virginia.gov/vacode/46.2-1110/" target="_blank" rel="noreferrer">46.2-1110</a> by more than one and one-half feet and not exceeding the length limitation as set forth in §§ <a href="http://law.lis.virginia.gov/vacode/46.2-1112/" target="_blank" rel="noreferrer">46.2-1112</a> and <a href="http://law.lis.virginia.gov/vacode/46.2-1113/" target="_blank" rel="noreferrer">46.2-1113</a> by more than three feet. The Commissioner and local authorities may designate the routes such permittees shall use from the port to the assembly plant.</p><p>1973, c. 62, § 46.1-343.3; 1989, c. 727; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>.</p>',3),n=[i];function s(h,l,c,d,g,p){return t(),r("div",null,n)}const _=e(a,[["render",s]]);export{m as __pageData,_ as default};
