import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-1149.1 Excess tandem axle weight permits for cotton module haulers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1149.1.md","filePath":"vacode/46.2-1149.1.md"}'),i={name:"vacode/46.2-1149.1.md"},o=r('<h1 id="§-46-2-1149-1-excess-tandem-axle-weight-permits-for-cotton-module-haulers" tabindex="-1">§ 46.2-1149.1 Excess tandem axle weight permits for cotton module haulers. <a class="header-anchor" href="#§-46-2-1149-1-excess-tandem-axle-weight-permits-for-cotton-module-haulers" aria-label="Permalink to &quot;§ 46.2-1149.1 Excess tandem axle weight permits for cotton module haulers.&quot;">​</a></h1><p>The Commissioner, upon application made by the owner or operator of vehicles used exclusively to transport seed cotton modules, shall issue a permit authorizing the operation on the highway of such vehicles, from September 1 through December 31 of each year, at tandem axle weights in excess of that authorized in § <a href="http://law.lis.virginia.gov/vacode/46.2-1125/" target="_blank" rel="noreferrer">46.2-1125</a>. The Commissioner may promulgate regulations governing such permits. Such permits shall allow the vehicles to have tandem axle weights of no more than 44,000 pounds. No permit issued under this section shall authorize a single axle weight in excess of that authorized in § <a href="http://law.lis.virginia.gov/vacode/46.2-1124/" target="_blank" rel="noreferrer">46.2-1124</a> or a gross weight in excess of 56,000 pounds.</p><p>The fee for a permit issued under this section shall be $45, to be allocated as follows: (i) $40 to the Highway Maintenance and Operating Fund established pursuant to § <a href="http://law.lis.virginia.gov/vacode/33.2-1530/" target="_blank" rel="noreferrer">33.2-1530</a>, with a portion equal to the percentage of the Commonwealth&#39;s total lane miles represented by the lane miles eligible for maintenance payments pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/33.2-319/" target="_blank" rel="noreferrer">33.2-319</a> and <a href="http://law.lis.virginia.gov/vacode/33.2-366/" target="_blank" rel="noreferrer">33.2-366</a> being redistributed on the basis of lane miles to the applicable localities pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/33.2-319/" target="_blank" rel="noreferrer">33.2-319</a> and <a href="http://law.lis.virginia.gov/vacode/33.2-366/" target="_blank" rel="noreferrer">33.2-366</a>, to be used to assist in funding needed highway pavement and bridge maintenance and rehabilitation and (ii) a $5 administrative fee to the Department.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0419" target="_blank" rel="noreferrer">419</a>; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0029" target="_blank" rel="noreferrer">29</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0036" target="_blank" rel="noreferrer">36</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0314" target="_blank" rel="noreferrer">314</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0443" target="_blank" rel="noreferrer">443</a>.</p>',4),n=[o];function l(s,h,g,c,d,p){return a(),t("div",null,n)}const u=e(i,[["render",l]]);export{m as __pageData,u as default};
