import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 46.2-1149.6 Permits for truck cranes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1149.6.md","filePath":"vacode/46.2-1149.6.md"}'),i={name:"vacode/46.2-1149.6.md"},o=a('<h1 id="§-46-2-1149-6-permits-for-truck-cranes" tabindex="-1">§ 46.2-1149.6 Permits for truck cranes. <a class="header-anchor" href="#§-46-2-1149-6-permits-for-truck-cranes" aria-label="Permalink to &quot;§ 46.2-1149.6 Permits for truck cranes.&quot;">​</a></h1><p>The Commissioner and local authorities of cities and towns, in their respective jurisdictions, may, upon written application made by an owner or operator and subject to the requirements of § <a href="http://law.lis.virginia.gov/vacode/46.2-1139/" target="_blank" rel="noreferrer">46.2-1139</a>, issue permits authorizing the operation over the highways of truck cranes that exceed the maximum weight specified in this title. Truck cranes that have been mounted with counterweights and other manufactured equipment that enable a single person to assemble and operate the truck crane shall be considered irreducible, and no application for a permit under this section shall be denied because of the applicant&#39;s refusal to remove such counterweights or other manufactured equipment.</p><p>2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0068" target="_blank" rel="noreferrer">68</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0258" target="_blank" rel="noreferrer">258</a>.</p>',3),n=[o];function s(c,h,l,d,p,u){return t(),r("div",null,n)}const f=e(i,[["render",s]]);export{m as __pageData,f as default};
