import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 46.2-1157 Inspection of motor vehicles required.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1157.md","filePath":"vacode/46.2-1157.md"}'),o={name:"vacode/46.2-1157.md"},a=t('<h1 id="§-46-2-1157-inspection-of-motor-vehicles-required" tabindex="-1">§ 46.2-1157 Inspection of motor vehicles required. <a class="header-anchor" href="#§-46-2-1157-inspection-of-motor-vehicles-required" aria-label="Permalink to &quot;§ 46.2-1157 Inspection of motor vehicles required.&quot;">​</a></h1><p>A. The owner or operator of any motor vehicle, trailer, or semitrailer registered in Virginia and operated or parked on a highway within the Commonwealth shall submit his vehicle to an inspection of its mechanism and equipment by an official inspection station, designated for that purpose, in accordance with § <a href="/vacode/46.2-1158/">46.2-1158</a>. No owner or operator shall fail to submit a motor vehicle, trailer, or semitrailer operated or parked on the highways in the Commonwealth to such inspection or fail or refuse to correct or have corrected in accordance with the requirements of this title any mechanical defects found by such inspection to exist.</p><p>B. The provisions of this section requiring safety inspections of motor vehicles shall also apply to vehicles used for firefighting; inspections of firefighting vehicles shall be conducted pursuant to regulations promulgated by the Superintendent of State Police, taking into consideration the special purpose of such vehicles and the conditions under which they operate.</p><p>C. Each day during which such motor vehicle, trailer, or semitrailer is operated or parked on any highway in the Commonwealth after failure to comply with this law shall constitute a separate offense.</p><p>D. Except as otherwise provided, autocycles shall be inspected as motorcycles under this article.</p><p>E. No law-enforcement officer shall stop a motor vehicle due to an expired vehicle inspection sticker until the first day of the fourth month after the original expiration date. No evidence discovered or obtained as the result of a stop in violation of this subsection, including evidence discovered or obtained with the operator&#39;s consent, shall be admissible in any trial, hearing, or other proceeding.</p><p>Code 1950, § 46-317; 1950, p. 691; 1958, c. 541, § 46.1-315; 1962, c. 246; 1978, cc. 275, 605; 1982, c. 646; 1989, c. 727; 1991, cc. 107, 717; 1993, c. 134; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0267" target="_blank" rel="noreferrer">267</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0796" target="_blank" rel="noreferrer">796</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0075" target="_blank" rel="noreferrer">75</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0137" target="_blank" rel="noreferrer">137</a>; 2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0514" target="_blank" rel="noreferrer">514</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0053" target="_blank" rel="noreferrer">53</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0256" target="_blank" rel="noreferrer">256</a>; 2020, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0051" target="_blank" rel="noreferrer">51</a>.</p>',7),n=[a];function l(c,s,h,p,f,g){return r(),i("div",null,n)}const v=e(o,[["render",l]]);export{u as __pageData,v as default};
