import{_ as e,o as i,c as n,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 46.2-1190.6 Other grounds for denying, suspending, or revoking licenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/46.2-1190.6.md","filePath":"vacode/46.2-1190.6.md"}'),r={name:"vacode/46.2-1190.6.md"},t=o('<h1 id="§-46-2-1190-6-other-grounds-for-denying-suspending-or-revoking-licenses" tabindex="-1">§ 46.2-1190.6 Other grounds for denying, suspending, or revoking licenses. <a class="header-anchor" href="#§-46-2-1190-6-other-grounds-for-denying-suspending-or-revoking-licenses" aria-label="Permalink to &quot;§ 46.2-1190.6 Other grounds for denying, suspending, or revoking licenses.&quot;">​</a></h1><p>A license issued pursuant to this article may be denied, suspended, or revoked on any one or more of the following grounds, where applicable:</p><ol><li><p>Material misstatement or omission in application for license;</p></li><li><p>Failure to comply subsequent to receipt of a written notice from the Department or any willful failure to comply with a lawful order, any provision of this article, or any term, condition, or restriction of a license;</p></li><li><p>Failure to comply with zoning or other land use regulations, ordinances, or statutes;</p></li><li><p>Use of deceptive business acts or practices;</p></li><li><p>Knowingly advertising by any means any assertion, representation, or statement of fact that is untrue, misleading, or deceptive relating to the conduct of the business for which a license is held or sought;</p></li><li><p>Having been found, through a judicial or administrative hearing, to have committed fraudulent or deceptive acts in connection with the training center for which a license is held or sought, or any consumer-related fraud;</p></li><li><p>Having been convicted of any criminal act involving the training center for which a license is held or sought;</p></li><li><p>Improper assignment, lending, or otherwise allowing the improper use of a license;</p></li><li><p>Any corporate officer, owner, administrator and any individual authorized to obligate the training center having been convicted of a felony;</p></li><li><p>Any corporate officer, owner, administrator and any individual authorized to obligate the training center having been convicted of any misdemeanor involving lying, cheating, stealing, or moral turpitude;</p></li><li><p>Failure to furnish the Department information, documentation, or records required or requested pursuant to this article;</p></li><li><p>Knowingly and willfully filing any false report, account, record, or memorandum;</p></li><li><p>Willfully altering or changing the appearance or wording of a training center license or a course completion certificate;</p></li><li><p>Failure to provide services in accordance with the terms, limitations, conditions, or requirements of the license; or</p></li><li><p>Failure to comply with other state and federal requirements relating to training center operations.</p></li></ol><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0734" target="_blank" rel="noreferrer">734</a>.</p>',4),a=[t];function l(s,c,d,p,u,h){return i(),n("div",null,a)}const m=e(r,[["render",l]]);export{f as __pageData,m as default};
